package com.example.cobauts1;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;
import android.widget.Adapter;

import com.example.cobauts1.adapter.LayananAdapter;
import com.example.cobauts1.models.TeamLogo;

import java.util.ArrayList;
import java.util.List;

public class LayananActivity extends AppCompatActivity {
    private ArrayList<TeamLogo> teams;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_layanan);
        RecyclerView teamsView = findViewById(R.id.rv_teams);
        teamsView.setHasFixedSize(true);

        teams = new ArrayList<>();
        dataSet();
//        teams.add(new TeamLogo("https://i.pinimg.com/originals/56/e4/25/56e42528614897a85bfc8f087a3df019.png", "Zumba Class",
//                "IDR 25.000/session"));
//        teams.add(new TeamLogo("data:image/jpeg;base64,",
//                "Yoga Class", "IDR 35.000/session"));
//        teams.add(new TeamLogo("data:image/png;base64,",
//                "Pound Fit Class", "IDR 40.000/session"));

        LayananAdapter adapter = new LayananAdapter(this, teams);
        teamsView.setAdapter(adapter);

        RecyclerView.LayoutManager layoutManager = new LinearLayoutManager(this);
        teamsView.setLayoutManager(layoutManager);
    }

    private void dataSet() {
        teams.add(new TeamLogo(R.drawable.zumba, "Zumba Class", "IDR 25.000/session"));
        teams.add(new TeamLogo(R.drawable.yoga, "Yoga Class", "IDR 35.000/session"));
        teams.add(new TeamLogo(R.drawable.poundfit, "Pound Fit Class", "IDR 40.000/session"));
    }
}
